import React from 'react';
import style from './style.js'
import newSimpleForm from './newSimpleForm.js'



const cookieButton = ({score}) => (
    score = 0,
    increaseScore({
        score: score + 1,
    }),
    <div>
        <button type="button" style={style.img} onClick={increaseScore()} >

        </button>
        <font size="10" style={style.score}>
            score = {score}!
        </font>
        <script src="updateScore.js"></script>
    </div>     

);



export default cookieButton;
