import React from "react";
import Score from './score.js'
import style from './style.js'
import cookieButton from "./cookieButton.js";

class newSimpleForm extends React.Component {
    state = {
        score: 0,
    }
    onButtonClick = () => {
        this.setState({
            score: this.state.score + 1,
        })
    }
    cookieButton = ({onClick}) => (
        <div>
            <button type="button" style={style.img} >
                <img src="https://cdn.discordapp.com/attachments/729502632963473499/1199467432843149322/chocolate-chip-cookie-butter-cookie-cookie-png-5869876d39c1c6e51df3cbfc44b0ac91.png?ex=65c2a619&is=65b03119&hm=e7dedd32561cea991da74b791e94bd835f49def734c0bda47059521c9db55e4a&" style={style.cookie}/>
            </button>
            <font size="10" style={style.score}>
                score = {this.state.score}!
            </font>
            <script src="updateScore.js"></script>
        </div>     

    );


    render() {
        const { score } = this.state;
        return (
            <div>
                <Score score={score}/>
                <cookieButton onButtonClick/>
            </div>
        )
    }

}

export default newSimpleForm;
