import React from 'react';
import style from './style.js'
import newSimpleForm from './newSimpleForm.js'


const cookieButton = ({score=0}) => (
    <div>
        <button type="button" style={style.img} onClick={(score) => score++} >
        </button>
        <font size="10" style={style.score}>
            score = {score}
        </font>
        <script src="updateScore.js"></script>
    </div>     

);



export default cookieButton;
