import React from "react";
import Score from './score.js'
import cookieButton from "./cookieButton.js";

class newSimpleForm extends React.Component {
    state = {
        score: 0,
    }
    onButtonClick = () => {
        this.setState({
            score: this.state.score + 1,
        })
    }

    render() {
        const { score } = this.state;
        return (
            <div>
                <Score score={score}/>
                <cookieButton />
            </div>
        )
    }

}

export default newSimpleForm;
